/*-------------------------------------------------
 サイト基本情報画面
-------------------------------------------------*/
$(function(){
	if ($('.btn-system-status').length){
		/**
		 * 切替ボタン押下時
		 */
		$(".btn-system-status").click(function() {	
			var curStatus = $(".lbl-system-status span").text();
			var message;
			if (curStatus === '稼働中') {
				message = 'メンテナンスに切替えてよろしいですか？';	
			} 
			if (confirm(message)) {
				if (confirm('本当によろしいですか？')) {
					return true;
				}
			}
			return false;		
		});
	}
});


















































